use ggstd::bytes;
use ggstd::encoding::base64;
use ggstd::image::{color, png, Image};

fn main() {
    example_decode();
}

const GOPHER: &[u8] = b"";

fn example_decode() {
    let image_data = base64::get_std_encoding().decode_to_vec(GOPHER).unwrap();
    let mut r = bytes::Reader::new(&image_data);

    // This example uses png.Decode which can only decode PNG images.
    // Consider using the general image.Decode as it can sniff and decode any registered image format.
    let img = png::decode(&mut r).unwrap();

    let levels = [" ", "░", "▒", "▓", "█"];

    for y in img.bounds().min.y..img.bounds().max.y {
        for x in img.bounds().min.x..img.bounds().max.x {
            let c = color::Gray::new_from(&img.at(x, y));
            let mut level = c.y / 51; // 51 * 5 = 255
            if level == 5 {
                level -= 1;
            }
            print!("{}", levels[level as usize]);
        }
        println!();
    }
}
